import React, { useEffect, useState } from "react";
import { api } from "../../api/api";


export default function GerenciarIdosos() {
    const [lista, setLista] = useState([]);
    const [loading, setLoading] = useState(false);


    useEffect(() => {
        setLoading(true);
        api.get('/admin/idosos')
            .then(res => setLista(res.data))
            .catch(err => console.error(err))
            .finally(() => setLoading(false));
    }, []);


    async function remover(id) {
        if (!confirm('Remover esse idoso?')) return;
        await api.delete(`/admin/idosos/${id}`);
        setLista(prev => prev.filter(p => p.id !== id));
    }


    return (
        <div style={{ padding: 20 }}>
            <h3>Gerenciar Idosos</h3>
            {loading ? <p>Carregando...</p> : (
                <ul>
                    {lista.map(i => (
                        <li key={i.id} style={{ display: 'flex', justifyContent: 'space-between', gap: 8 }}>
                            <span>{i.nome}</span>
                            <div>
                                <button onClick={() => /* navegar pra editar */ null}>Editar</button>
                                <button onClick={() => remover(i.id)} style={{ marginLeft: 8 }}>Remover</button>
                            </div>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
}
