import React, { useEffect, useState } from "react";
import { api } from "../../api/api";
import { useAuth } from "../../contexts/AuthContext";

export default function DashboardFamiliar() {
    const { user } = useAuth();
    const [idosos, setIdosos] = useState([]);

    useEffect(() => {
        async function fetchIdosos() {
            try {
                const res = await api.get(`/familiares/${user.id}/idosos`);
                setIdosos(res.data);
            } catch (err) {
                console.error(err);
            }
        }
        fetchIdosos();
    }, [user.id]);

    return (
        <div>
            <h2>Dashboard Familiar</h2>
            <ul>
                {idosos.map((i) => (
                    <li key={i.id}>{i.nome} - {i.email}</li>
                ))}
            </ul>
        </div>
    );
}
