import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { api } from "../../api/api";
import Input from "../../components/Form/Input";


export default function Cadastro() {
    const [form, setForm] = useState({ nome: "", email: "", senha: "", tipo: "IDOSO" });
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();


    function change(e) {
        const { name, value } = e.target;
        setForm(prev => ({ ...prev, [name]: value }));
    }


    async function submit(e) {
        e.preventDefault();
        setLoading(true);
        try {
            await api.post("/auth/cadastro", form);
            alert("Cadastro realizado com sucesso");
            navigate("/");
        } catch (err) {
            alert("Erro ao cadastrar: " + (err?.response?.data?.message || err.message));
        } finally { setLoading(false); }
    }


    return (
        <div style={{ maxWidth: 520, margin: "30px auto" }}>
            <h2>Cadastrar</h2>
            <form onSubmit={submit}>
                <Input label="Nome" name="nome" value={form.nome} onChange={change} required />
                <Input label="Email" name="email" type="email" value={form.email} onChange={change} required />
                <Input label="Senha" name="senha" type="password" value={form.senha} onChange={change} required />
                <label style={{ display: "block", marginBottom: 10 }}>
                    Tipo
                    <select name="tipo" value={form.tipo} onChange={change} style={{ width: "100%", padding: 8, borderRadius: 6 }}>
                        <option value="IDOSO">Idoso</option>
                        <option value="FAMILIAR">Familiar</option>
                        <option value="ADMIN">Administrador</option>
                    </select>
                </label>
                <div style={{ display: "flex", gap: 10 }}>
                    <button type="submit" disabled={loading} style={{ padding: 10, borderRadius: 8 }}>{loading ? "Criando..." : "Criar conta"}</button>
                    <button type="button" onClick={() => navigate("/")} style={{ padding: 10, borderRadius: 8 }}>Voltar</button>
                </div>
            </form>
        </div>
    );
}
