import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../../contexts/AuthContext";
import Input from "../../components/Form/Input";


export default function Login() {
    const [email, setEmail] = useState("");
    const [senha, setSenha] = useState("");
    const [loading, setLoading] = useState(false);
    const { login } = useAuth();
    const navigate = useNavigate();


    async function handleSubmit(e) {
        e.preventDefault();
        setLoading(true);
        try {
            const user = await login({ email, senha });
// redireciona conforme tipo
            if (user.tipo === "IDOSO") navigate("/idoso/dashboard");
            else if (user.tipo === "FAMILIAR") navigate("/familiar/dashboard");
            else navigate("/admin/dashboard");
        } catch (err) {
            alert("Erro no login: " + (err?.response?.data?.message || err.message));
        } finally {
            setLoading(false);
        }
    }


    return (
        <div style={{ maxWidth: 420, margin: "40px auto" }}>
            <h2>Login</h2>
            <form onSubmit={handleSubmit}>
                <Input label="E-mail" type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
                <Input label="Senha" type="password" value={senha} onChange={(e) => setSenha(e.target.value)} required />
                <div style={{ display: "flex", gap: 10 }}>
                    <button type="submit" disabled={loading} style={{ padding: 10, borderRadius: 8 }}>{loading ? "Entrando..." : "Entrar"}</button>
                    <button type="button" onClick={() => navigate("/auth/cadastro")} style={{ padding: 10, borderRadius: 8 }}>Criar conta</button>
                </div>
            </form>
        </div>
    );
}
