import React from "react";


export default function Card({ title, description, onClick, children }) {
    return (
        <div style={{
            border: "1px solid #e6e6e6",
            borderRadius: 12,
            padding: 18,
            width: 240,
            background: "#fff",
            boxShadow: "0 2px 6px rgba(0,0,0,0.04)",
            cursor: onClick ? "pointer" : "default",
        }} onClick={onClick}>
            <h3 style={{ margin: 0, marginBottom: 8 }}>{title}</h3>
            {description && <p style={{ marginTop: 0, color: "#666" }}>{description}</p>}
            {children}
        </div>
    );
}
