import React, { useEffect, useState } from "react";
import { useAuth } from "../../contexts/AuthContext";
import { api } from "../../api/api";

export default function DashboardFamiliar() {
    const { user, logout } = useAuth();
    const [idosos, setIdosos] = useState([]);

    useEffect(() => {
        async function fetchIdosos() {
            try {
                const res = await api.get(`/familiares/${user.id}/idosos`);
                setIdosos(res.data);
            } catch (err) {
                console.error("Erro ao buscar idosos:", err);
            }
        }
        fetchIdosos();
    }, [user.id]);

    return (
        <div style={{ padding: 20 }}>
            <h1>Ol√°, {user?.nome}!</h1>
            <h2>Idosos vinculados:</h2>
            <ul>
                {idosos.map((i) => (
                    <li key={i.id}>
                        {i.nome} - {i.dataNasc} - {i.email}
                    </li>
                ))}
            </ul>
            <button onClick={logout}>Sair</button>
        </div>
    );
}
